import React, { useState } from 'react';
import './AddUser.css';
import Card from './UI/Card';
import Button from './Button';
import UserList from './UserList';

const AddUser = () => {
  const [userName, setUserName] = useState('');
  const [userAge, setUserAge] = useState('');
  const [userInfo, setUserInfo] = useState('');

  // 변수 State
  const userNameChangeHandler = (event) => {
    setUserName(event.target.value);
    console.log(event);
  };

  // 객체 State
  const userAgeChangeHandler = (event) => {
    setUserAge((prevState) => {
      return { ...prevState, enteredUserAge: +event.target.value };
    });
  };

  const addUserHandler = (event) => {
    event.preventDefault();
    const userInfo = {
      enteredUserName: userName,
      enteredUserAge: userAge.enteredUserAge,
    };
    console.log(userInfo);

    setUserName('');
    setUserAge({ enteredUserAge: '' });
  };

  return (
    <div>
      <Card className='input'>
        <form onSubmit={addUserHandler}>
          <label>이름</label>
          <input
            type='text'
            value={userName}
            onChange={userNameChangeHandler}
          />
          <label>나이</label>
          <input
            type='number'
            value={userAge.enteredUserAge || ''}
            onChange={userAgeChangeHandler}
            step='1'
          />
          <Button type='submit'>유저 추가</Button>
        </form>
      </Card>
      {!userInfo ? '' : <UserList usersInf={userInfo} />}
    </div>
  );
};

export default AddUser;
