import React, { useState } from 'react';
import './AddUser.css';
import Card from './UI/Card';

const AddUser = () => {
  const [userName, setUserName] = useState('');
  const [userAge, setUserAge] = useState('');

  const userNameChangeHandler = (event) => {
    setUserName(event.target.value);

    console.log(event);
    //  setUserName((prevState) => {
    //    return { ...prevState, enteredUserName: event.target.value };
    //  });
  };

  const userAgeChangeHandler = (event) => {
    setUserAge((prevState) => {
      return { ...prevState, enteredUserAge: event.target.value };
    });
  };

  const addUserHandler = (event) => {
    event.preventDefault();
    const userInfo = {
      enteredUserName: userName,
      enteredUserAge: userAge,
    };
    console.log(userInfo);

    setUserName('');
    setUserAge('');
  };

  return (
    <Card className='input'>
      <form onSubmit={addUserHandler}>
        <label>이름</label>
        <input
          type='text'
          value={userName.enteredUserName}
          onChange={userNameChangeHandler}
        />
        <label>나이</label>
        <input type='number' onChange={userAgeChangeHandler} />
        <button type='submit'>유저 추가</button>
      </form>
    </Card>
  );
};

export default AddUser;
