import React, { useState } from 'react';
import './AddUser.css';
import Card from './UI/Card';
import Button from './UI/Button';
import UserList from './UserList';
import ErrorModal from './UI/ErrorModal';

const AddUser = () => {
  const [userName, setUserName] = useState('');
  const [userAge, setUserAge] = useState('');
  const [userInfo, setUserInfo] = useState([]);
  const [emptyMessage, setEmptyMessage] = useState();
  const [isInvalid, setIsInvalid] = useState(false);

  // 변수 State
  const userNameChangeHandler = (event) => {
    setUserName(event.target.value);
  };

  // 객체 State
  const userAgeChangeHandler = (event) => {
    setUserAge((prevState) => {
      return { ...prevState, enteredUserAge: +event.target.value };
    });
  };

  const addUserHandler = (event) => {
    event.preventDefault();
    if (userName === '' || userAge === '') {
      return setIsInvalid(true);
    } else if (userAge < 0) {
      return setIsInvalid(true);
    }

    const userInfo = {
      id: Math.random().toString(),
      enteredUserName: userName,
      enteredUserAge: userAge.enteredUserAge,
    };
    console.log('In addUserHandler', userInfo);
    setUserInfo((prevState) => {
      return [...prevState, userInfo];
    });

    setUserName('');
    setUserAge({ enteredUserAge: '' });
  };
  console.log(isInvalid);

  return (
    <div>
      {!isInvalid ? '' : <ErrorModal />}
      <Card className='input'>
        <form onSubmit={addUserHandler}>
          <label>이름</label>
          <input
            type='text'
            value={userName}
            onChange={userNameChangeHandler}
          />
          <label>나이</label>
          <input
            type='number'
            value={userAge.enteredUserAge || ''}
            onChange={userAgeChangeHandler}
            step='1'
          />
          <Button type='submit'>추가</Button>
        </form>
      </Card>
      {userInfo.length > 0 ? <UserList userinfo={userInfo} /> : ''}
    </div>
  );
};

export default AddUser;
